mapper.select:
select id, name title,
(select count(*) from t_question where catalogId = c.id) allCount,
(select count(*) from tr_member_question where memberId = #{memberId}
and questionId in (select id from t_question where catalogId = c.id)) finishedCount
from t_catalog c
where courseId = #{courseId}

select id, shortName title,
(select count(*) from t_question where companyId = c.id) allCount,
(select count(*) from tr_member_question where memberId = #{memberId}
and questionId in (select id from t_question where companyId = c.id)) finishedCount
from t_company c
where cityId = #{cityId}
